mapper.insert:
insert into VINUX_DESIGN_PAGE_DATA (ID, PAGE_ID, CDATE, 
  _VERSION, DATA
  )
values (#{id,jdbcType=BIGINT}, #{pageId,jdbcType=BIGINT}, #{cdate,jdbcType=TIMESTAMP}, 
  #{version,jdbcType=BIGINT}, #{data,jdbcType=LONGVARCHAR}
  )

mapper.update:
update VINUX_DESIGN_PAGE_DATA
set PAGE_ID = #{pageId,jdbcType=BIGINT},
  CDATE = #{cdate,jdbcType=TIMESTAMP},
  _VERSION = #{version,jdbcType=BIGINT},
  DATA = #{data,jdbcType=LONGVARCHAR}
where ID = #{id,jdbcType=BIGINT}

update VINUX_DESIGN_PAGE_DATA
set PAGE_ID = #{pageId,jdbcType=BIGINT},
  CDATE = #{cdate,jdbcType=TIMESTAMP},
  _VERSION = #{version,jdbcType=BIGINT}
where ID = #{id,jdbcType=BIGINT}

mapper.select:
SELECT
,
from VINUX_DESIGN_PAGE_DATA WHERE ID = (SELECT MAX(ID) from VINUX_DESIGN_PAGE_DATA WHERE PAGE_ID = #{pageId,jdbcType=BIGINT})
